import React from "react"

import ContactCard from "./ContactCard"
import ContactCardsList from "./ContactCardsList"

class App extends React.Component{
    constructor(){
        super()
        this.state={
            isLoggedIn : false,
            isLoggedColor : "red"
        }
    }

    loginApp(){
        this.setState(prevState =>{
            return {
                isLoggedIn: true,
                isLoggedColor : "green"
            }
        })
    }

    logoutApp(){
        this.setState(prevState =>{
            return {
                isLoggedIn: false,
                isLoggedColor : "red"
            }
        })
    }

    render(){
        const contactsList = ContactCardsList.map(obj => <ContactCard 
                                                            key={obj.id}
                                                            name={obj.name}
                                                            imgUrl={obj.imgUrl}
                                                            phone={obj.phone}
                                                            email={obj.email}
                                                            />)
        return (
            <div className="contacts">
				<h1 style={color=this.state.isLoggedColor}>{this.state.isLoggedIn}</h1>
                <button onClick={this.loginApp}>Login</button>
                <button onClick={this.logoutApp}>Logout</button>
                <ChildComponent isLoggedIn={this.isLoggedIn, this.isLoggedColor}/>
                {contactsList}
            </div>
        )
    }
}

export default App
